.user-contents {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 1.2em;
    /*text-align: justify;*/
    word-wrap: break-word;
}

.article-authors, .article-keywords {
    line-height: 1.6em;
}

.article-title {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 48px;
    font-weight: 700;
}

.article-subtitle {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 40px;
    font-weight: 900;
}

.article-abstract {
    margin-left: 50px;
    margin-right: 50px;
}

.article-part.metadata {
    margin-bottom: 20px;
}

.pagination-footnote:before,
.pagination-footnote > * > *:first-child:before {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 9px;
    text-align: left;
    vertical-align: super;
    line-height: 0;
}

.pagination-footnote > * > *:first-child:before {
    margin-right: 5px;
}

.pagination-footnote > * > * {
    font-family: 'Noticia Text', serif;
    font-weight: 400;
    font-size: 10px;
}

.pagination-pagenumber {
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 12px;
    font-weight: 700;
}

.pagination-footnotes {
    margin-top: 25px;
}

